CREATE TABLE "public"."greffier" ("id" serial NOT NULL, "user_id" integer NOT NULL, "ti_id" integer NOT NULL, "share_email" boolean NOT NULL DEFAULT false, PRIMARY KEY ("id") , FOREIGN KEY ("user_id") REFERENCES "public"."users"("id") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("ti_id") REFERENCES "public"."tis"("id") ON UPDATE cascade ON DELETE cascade, UNIQUE ("user_id", "ti_id"));
